void	FlushSocket(SOCKET Socket, Host CurHost)
{
	fd_set			m_TCPSockets;
	int				SelRes = 0;
	int				Res = -1;
	timeval			Wait = {0, 0};
	uchar			Buffer[0x1000] = {0};
	

	RecvBufferSz = 0xFFFF;
	ZeroMemory(RecvBuffer, 0xFFFF);

	Res = -1;
	FD_ZERO(&m_TCPSockets);
	FD_SET(Socket, &m_TCPSockets);
		
	RecvBufferSz = 0;
	while ((SelRes = select(FD_SETSIZE, &m_TCPSockets, NULL, NULL, &Wait)))
	{	
		Res = recv(Socket, (char *)&(Buffer[0]), sizeof(Buffer), 0);
		if (Res == 0)
		{
			printf("Connection reset by peer !\n");
			return ;
		}
		else if (Res == -1)
		{
			printf("Socket Error\n");
			return ;
		}
		else
		{
			printf("%d Bytes Flushed..\n", Res);
			memcpy_s(RecvBuffer + RecvBufferSz, 0xFFFF - RecvBufferSz, Buffer, Res);
			RecvBufferSz += Res;
		}

		FD_ZERO(&m_TCPSockets);
		FD_SET(Socket, &m_TCPSockets);
	}
	
	if ((Res == -1) || (Res == 0))
		return ;
	return ;
}

int		SendPacket(SOCKET Socket, Host CurHost, uchar *Packet, uint Size, sockaddr_in *PReplyTo)
{
	sockaddr_in		Sender, ReplyTo;
	fd_set			m_UDPSockets;
	timeval			Wait = {1, 300000};
	int				SelRes = 0;
	int				Res = -1;
	uchar			Buffer[0x1000];
	int				RptSz;
	
	RecvBufferSz = 0xFFFF;
	RptSz = sizeof(struct sockaddr_in);
	if (!PReplyTo) PReplyTo = &ReplyTo;

	ZeroMemory(RecvBuffer, 0xFFFF);
	ZeroMemory((char *)&Sender, sizeof(Sender));
	Sender.sin_family = AF_INET;
	Sender.sin_port = htons(CurHost.port);
	Sender.sin_addr.s_addr = inet_addr(CurHost.ip);

	Res = sendto(Socket, (const char *)Packet, Size, 0, (SOCKADDR *)&Sender, sizeof(Sender));
	printf("%d Bytes sent..\n", Res);

	if ((Res == -1) || (Res == 0))
		return (0);
		
	Res = -1;
	FD_ZERO(&m_UDPSockets);
	FD_SET(Socket, &m_UDPSockets);

	RecvBufferSz = 0;
	while ((SelRes = select(FD_SETSIZE, &m_UDPSockets, NULL, NULL, &Wait)))
	{	
		RptSz = sizeof(struct sockaddr_in);
		Res = recvfrom(Socket, (char *)&Buffer[0], sizeof(Buffer), 0, (SOCKADDR *)PReplyTo, &RptSz);
		if (Res == 0)
		{
			printf("Connection reset by peer !\n");
			return (0);
		}
		else if (Res == -1)
		{
			printf("Socket Error\n");
			return (0);
		}
		else
		{
			printf("%d Bytes in response..\n", Res);
			memcpy_s(RecvBuffer + RecvBufferSz, 0xFFFF - RecvBufferSz, Buffer, Res);
			RecvBufferSz += Res;
		}

		FD_ZERO(&m_UDPSockets);
		FD_SET(Socket, &m_UDPSockets);
	}

	if ((Res == -1) || (Res == 0))
		return (0);
	return (1);
}

int		SendPacketTCP(SOCKET Socket, Host CurHost, uchar *Packet, uint Size, ushort CustomPort, int *Connected)
{
	sockaddr_in		Sender;
	fd_set			m_TCPSockets;
	timeval			Wait = {2, 0};
	int				SelRes = 0;
	int				Res = -1;
	uchar			Buffer[0x1000];
	

	RecvBufferSz = 0xFFFF;
	ZeroMemory(RecvBuffer, 0xFFFF);

	if (SuperWait)
		Wait.tv_sec = (SuperWait == 1) ? 10 : 5;

	if (!(*Connected))
	{
		ZeroMemory((char *)&Sender, sizeof(Sender));
		Sender.sin_family = AF_INET;
		Sender.sin_port = htons((CustomPort == -1) ? CurHost.port : CustomPort);
		Sender.sin_addr.s_addr = inet_addr(CurHost.ip);
		
		if (connect(Socket, (struct sockaddr *)&Sender, sizeof(Sender)) < 0)
		{
			printf("Connection refused..\n");
			return (0);
		}
		*Connected = 1;

		/*
		// my debug
		if (0){
			char dump_buf[0x1000];
			if (CustomPort == -1) {
				sprintf(dump_buf,"TCP connect to %s:%d host",CurHost.ip,CurHost.port);
			}else{
				sprintf(dump_buf,"TCP connect to %s:%d host",CurHost.ip,CustomPort);
			};
			dumpstr(dump_buf);
		};
		// end of my debug
		*/
	}

	Res = send(Socket, (const char *)Packet, Size, 0);
	printf("%d Bytes sent..\n", Res);

	if ((Res == -1) || (Res == 0))
		return (0);

	if (NoWait)
	{
		NoWait = 0;
		return (1);
	}
	Res = -1;
	FD_ZERO(&m_TCPSockets);
	FD_SET(Socket, &m_TCPSockets);
		
	RecvBufferSz = 0;
	while ((SelRes = select(FD_SETSIZE, &m_TCPSockets, NULL, NULL, (Blocking) ? NULL : &Wait)))
	{	
		Res = recv(Socket, (char *)&(Buffer[0]), sizeof(Buffer), 0);
		if (Res == 0)
		{
			printf("Connection reset by peer !\n");
			return (0);
		}
		else if (Res == -1)
		{
			printf("Socket Error %08X\n", WSAGetLastError());
			return (0);
		}
		else
		{
			printf("%d Bytes in response..\n", Res);
			memcpy_s(RecvBuffer + RecvBufferSz, 0xFFFF - RecvBufferSz, Buffer, Res);
			RecvBufferSz += Res;
		}

		FD_ZERO(&m_TCPSockets);
		FD_SET(Socket, &m_TCPSockets);

		if (SuperWait)
		{
			if (SuperWait == 2)
				break;
			Wait.tv_sec = 1;
			Wait.tv_usec = 0;
		}
		else
		{
			Wait.tv_sec = 0;
			Wait.tv_usec = 750000;
		}
		Blocking = 0;
	}
	
	SuperWait = 0;

	if ((Res == -1) || (Res == 0))
		return (0);
	return (1);
}

int		SendPacketTCPEx(SOCKET Socket, Host CurHost, uchar *Packet, uint Size, ushort CustomPort, int *Connected)
{
	sockaddr_in		Sender;
	fd_set			m_TCPSockets;
	timeval			Wait = {0, 1000000};
	int				SelRes = 0;
	int				Res = -1;
	RecvDesc		*RecvD;

	while (!RecvDQueue.empty())
		RecvDQueue.pop();

	if (!(*Connected))
	{
		ZeroMemory((char *)&Sender, sizeof(Sender));
		Sender.sin_family = AF_INET;
		Sender.sin_port = htons((CustomPort == -1) ? CurHost.port : CustomPort);
		Sender.sin_addr.s_addr = inet_addr(CurHost.ip);

		if (connect(Socket, (struct sockaddr *)&Sender, sizeof(Sender)) < 0)
		{
			printf("Connection refused..\n");
			return (0);
		}
		*Connected = 1;
	}

	Res = send(Socket, (const char *)Packet, Size, 0);
	printf("%d Bytes sent..\n", Res);

	if ((Res == -1) || (Res == 0))
		return (0);

	Res = -1;
	FD_ZERO(&m_TCPSockets);
	FD_SET(Socket, &m_TCPSockets);
		
	while ((SelRes = select(FD_SETSIZE, &m_TCPSockets, NULL, NULL, &Wait)))
	{	
		RecvD = (RecvDesc *)malloc(sizeof(RecvDesc));
		Res = recv(Socket, (char *)&(RecvD->RecvBuf[0]), sizeof(RecvD->RecvBuf), 0);
		if (Res == 0)
		{
			printf("Connection reset by peer !\n");
			return (0);
		}
		else
		{
			printf("%d Bytes in response..\n", Res);
			RecvD->RecvSz = Res;
		}

		RecvDQueue.push(*RecvD);

		FD_ZERO(&m_TCPSockets);
		FD_SET(Socket, &m_TCPSockets);
	}
	
	if ((Res == -1) || (Res == 0))
		return (0);
	return (1);
}

